const redis = require('redis');

// Create a Redis client
const client = redis.createClient();

// Handle connection errors
client.on('error', (err) => {
    console.error('Redis client error:', err);
});

// Set dummy values
client.mset('key1', 'value1', 'key2', 'value2', (err, reply) => {
    if (err) {
        console.error('Error setting values:', err);
    } else {
        console.log('Values set successfully:', reply);

        // Get values
        client.mget('key1', 'key2', (err, reply) => {
            if (err) {
                console.error('Error getting values:', err);
            } else {
                console.log('Retrieved values:', reply);

                // Close the connection
                client.quit((err) => {
                    if (err) {
                        console.error('Error closing connection:', err);
                    } else {
                        console.log('Connection closed successfully.');
                    }
                });
            }
        });
    }
});

